/**
 * Catalog product option select type
 *
 * @category   Mage
 * @package    Mage_Catalog
 * @author     Magento Core Team <core@magentocommerce.com>
 */
class Cmsmart_Pricematrix_Model_Catalog_Product_Option_Type_Cmsmart extends Mage_Catalog_Model_Product_Option_Type_Default
{
    public function isCustomizedView(){
        return true;
    }

    public function getCustomizedView($optionInfo){
        $customizeBlock = new Cmsmart_Pricematrix_Block_Catalog_Product_Option_Type_Cmsmart();
        $customizeBlock->setInfo($optionInfo);
        return $customizeBlock->toHtml();
    }
}
